/**
    * @description      : 
    * @author           : MLab
    * @group            : 
    * @created          : 07/10/2021 - 10:07:05
    * 
    * MODIFICATION LOG
    * - Version         : 1.0.0
    * - Date            : 07/10/2021
    * - Author          : MLab
    * - Modification    : 
**/
import React,{useState} from 'react';
import { Text, View, StyleSheet, TextInput, TouchableOpacity, SafeAreaView} from 'react-native';
import { Card } from 'react-native-paper';
import { FontAwesome } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { EvilIcons } from '@expo/vector-icons';
import {handleSignIn} from '../firebase'
import { AlertNote } from '../Components';
export default function SignIn({navigation}) {
  const 
    [email, setEmail] = useState(""),
    [password, setPassword] = useState(""),
    [displayModal, setDisplaModal] = useState(false)
  const Login = () =>{
    handleSignIn(email, password)
    setDisplaModal(true)
  }
  return (
    <View >
      <AlertNote visible={displayModal} setVisble={setDisplaModal}/>

      <Card style={styles.card}>
        <View style={styles.heartIcon}>
          <FontAwesome name="heartbeat" size={76} color="#fff" />
        </View>
        <Text style={{ color: '#fff', fontSize: 28, marginLeft: 15 }}> X-urgency </Text>
      </Card>

      <View style={styles.header}>
        <Text style={{ fontWeight: 'bold', fontSize: 18, paddingLeft: 10 }}>LogIn</Text>
      </View>

      <View>
        <Card style={styles.txtCards}> 
        <View style={{flexDirection: 'row'}}> 
        <AntDesign name="user" size={22} color="black"  />
        <TextInput style={styles.txtUser} 
          name= 'username' placeholder= 'Username' onChangeText={text=>setEmail(text)} 
        />
        </View>
        </Card>
        
        <Card style={styles.txtCards}> 
         <View style={{flexDirection: 'row'}}> 
           <EvilIcons name="lock" size={28} color="black" />
           <TextInput style={styles.txtPass} 
             name= 'password' placeholder= 'Password' onChangeText={text=>setPassword(text)}  
           />   
        </View>
        </Card>
        <TouchableOpacity  onPress= {() => {navigation.navigate('Reset Password')}}>
        <Text style= {{paddingLeft: 160, paddingBottom: 10, color: '#F47066'}}>Forgot Password? </Text>
        </TouchableOpacity>
        <TouchableOpacity style= {styles.signIn} onPress={Login}>
          <Text style= {{color: '#fff'}}>LOGIN </Text>
        </TouchableOpacity>
        <Text style={{ paddingTop: 5, paddingLeft: 100 }}>
          New User?
          <TouchableOpacity onPress={() => { navigation.navigate('Sign Up') }}>
            <Text style={{ color: '#F47066' }}> Sign Up</Text>
          </TouchableOpacity>
        </Text>
      </View>
    </View>
  )
}
const styles = StyleSheet.create({
  card: {
    backgroundColor: '#F47066',
    marginTop: 1,
    width: 362,
    height: 250,
    borderRadius: 30,
    alignItems: 'center',
  },
  heartIcon: {
    marginTop: 75,
    marginLeft: 45,
  },
  header: {
    paddingTop: 10,
    paddingLeft: 135,
  },
  txtUser: {
    borderRadius: 30,
    // outline: 'none',
    backgroundColor: 'lightgrey',
    padding: 5,
  },
  txtPass: {
    marginBottom: 15,
    borderRadius: 30,
    // outline: 'none',
    backgroundColor: 'lightgrey',
    padding: 2,
  },
  txtCards: {
    backgroundColor: 'lightgrey',
    width: 250,
    height: 30,
    borderRadius: 30,
    marginLeft: 40,
    marginTop: 10
  },
  signIn: {
    height: 30,
    width: 150,
    marginLeft: 90,
    borderRadius: 30,
    backgroundColor: '#F47066',
    alignItems: 'center',
    justifyContent: 'center',
  }
});
