        <AntDesign name="arrowleft" size={24} color="black" />
      </TouchableOpacity>      


      <View style={{ width: 315, marginLeft: 10 }}>
      
      {videoVisible && <Video
          source={{ uri: videoPlay }}
          rate={1.0}
          volume={1.0}
          isMuted
          useNativeControls
          resizeMode="contain"
          isLooping
          style={{ borderRadius: 25 }}
