import React, { useState } from 'react';
import { Text, View, StyleSheet, TextInput, SafeAreaView, Image, TouchableOpacity, Button } from 'react-native';
import { Card } from 'react-native-paper';
import { AntDesign } from '@expo/vector-icons';
import { Entypo } from '@expo/vector-icons';
import { FontAwesome5 } from '@expo/vector-icons';
import { Avatar } from 'react-native-elements';
import { Video, AVPlaybackStatus } from 'expo-av';
import BottomNav from '../Componen'



export default function Strokes({navigation}) {

  const video = React.useRef(null); 
  const [status, setStatus] = React.useState({});
  const [userName, setUserName] = useState('Rando123')  
  const link =  'http://d23dyxeqlo5psv.cloudfront.net/big_buck_bunny.mp4'
    
    //const {comments, setComments} = useState('')

  const [visibleStatusBar, setVisibleStatusBar] = useState(false);
  const changeVisibilityStatusBar = () => {
    setVisibleStatusBar(!visibleStatusBar);
  };
  const changeStyleStatusBar = () => {
    const styleId = styleTypes.indexOf(styleStatusBar) + 1;
    if (styleId === styleTypes.length) {
      return setStyleStatusBar(styleTypes[0]);
    }
    return setStyleStatusBar(styleTypes[styleId]);
  };


  

  return (
    <View style= {styles.contain}> 



      <Card style={styles.txtCards}>

        <View style={{ flexDirection: 'row' }}>
          <AntDesign name="search1" size={18} color="black" style={{ marginTop: 15, marginLeft: 8 }} />
          <TextInput style={styles.txtSearch}
            name='search' placeholder='Search'
          />
        </View>

      </Card>


      <View style={{ width: 315, marginTop: 50, marginLeft: 30 }}>
        
        <Video
          ref={video}
          source={{ uri: link }}
          useNativeControls
          resizeMode="contain"
          isLooping
          onPlaybackStatusUpdate={status => setStatus(() => status)}
          style={{ borderRadius: 25 }}
        />

      </View>


      <View style={styles.descriptionContainer}>
        <BottomNav />
      </View>
      
      <Card style={{ height: 40, width: 315, marginTop: 30, marginLeft: 30 }}>
        <Text style={{ paddingTop: 10, paddingLeft: 10 }}>Comments - 498</Text>
      </Card>
    </View>
  )
}
const styles = StyleSheet.create({
  header: {
    paddingLeft: 30,
    paddingTop: 50
  },
  txtSearch: {
    width: 320,
    height: 50,
    borderRadius: 10,
    outline: 'none',
    backgroundColor: 'lightgrey',
    paddingLeft: 10,
  },
  txtCards: {
    backgroundColor: 'lightgrey',
    width: 320,
    height: 50,
    borderRadius: 10,
    marginLeft: 28,
    marginTop: 25
  },
  strokeVid: {
    height: 180,
    width: 315,
    borderRadius: 30,
    marginTop: 50,
    marginLeft: 30
  },
  dropDown: {
    marginLeft: 100
  }
});
