**/
/**
    * @description      : 
    * @author           : MLab
    * @group            : 
    * @created          : 05/10/2021 - 14:22:53
    * 
    * MODIFICATION LOG
    * - Version         : 1.0.0
    * - Date            : 05/10/2021
    * - Author          : MLab
    * - Modification    : 
**/
import React, {useState} from 'react';
import { Text, View, StyleSheet, TextInput, SafeAreaView, Image, TouchableOpacity } from 'react-native';

import {Input} from 'react-native-elements';

import { Card } from 'react-native-paper';

import { AntDesign } from '@expo/vector-icons';
import strVid from '../images/stroke-vid.jpg';
import User from '../images/user.jpg'
import home from '../images/home.jpg'
import play from '../images/playMenu.jpg'

export default function Strokes({navigation}) {
    
    const {comments, setComments} = useState('')

    const styleTypes = ['default', 'dark-content', 'light-content'];
  const [visibleStatusBar, setVisibleStatusBar] = useState(false);
  const [styleStatusBar, setStyleStatusBar] = useState(styleTypes[0]);

  const changeVisibilityStatusBar = () => {
    setVisibleStatusBar(!visibleStatusBar);
  };

  const changeStyleStatusBar = () => {
    const styleId = styleTypes.indexOf(styleStatusBar) + 1;

    if (styleId === styleTypes.length) {
      return setStyleStatusBar(styleTypes[0]);
    }
    return setStyleStatusBar(styleTypes[styleId]);
  };

  return (
    <View style= {styles.contain}> 


      <Card style={styles.txtCards}>

          <View style={{ flexDirection: 'row'}}> 
        
            <AntDesign name="search1" size={18} color="black"  style= {{marginTop:15, marginLeft: 8}}/>
        
            <TextInput style={styles.txtSearch} 
              name= 'search' placeholder= 'Search' 
            />

          </View>

        </Card>

      

      


  
          
        <View>
          <Image style={styles.strokeVid} source= {strVid} />     
        
          <Text style={{paddingLeft: 50, fontWeight: 'bold'}}>Stroke</Text>
        </View>
      
  

      <View style= {{marginTop: 50, marginLeft: 30, width: 320}}>

        <Input 
          placeholder="Write a comment"
          leftIcon={{ type: 'font-awesome', name: 'comment' }}
          onChangeText={value => this.setState({ comments: value })}
        />

      </View>



      <View>

      <TouchableOpacity title="topNav" onPress={() => changeVisibilityStatusBar()} > 
        <Text>Click for Step-by-Step instructons.</Text>

      </TouchableOpacity>

      </View>


      
      <View style={styles.buttonContainer}>

      {!visibleStatusBar ? (
            
              <View></View>
           
          ) 
            
          : //Hidden Step-by-steps

            <Card
        style={{
          width: 280,
          height: 300,
          marginLeft: 20,
          borderRadius: 20,
          backgroundColor: 'pink',
        }}>
        <Text
          style={{
            paddingLeft: 10,
            paddingTop: 10,
            fontWeight: 'bold',
            fontSize: 16,
          }}>
          Comments:
        </Text>

        <View
          style={{
            flexDirection: 'row',
            marginTop: 10,
            marginLeft: 12,
            width: 255,
          }}>
          <Text>
            
            <SafeAreaView style={{ fontWeight: 'bold' }}>
              prince4code:
            </SafeAreaView>
            the app is awesome guys, I give you guys 10/10
          </Text>
        </View>

        <View
          style={{
            flexDirection: 'row',
            marginTop: 10,
            marginLeft: 12,
            width: 255,
          }}>
          <Text>
            
            <SafeAreaView style={{ fontWeight: 'bold' }}>
              prince4code:
            </SafeAreaView>
            the app is awesome guys, I give you guys 10/10
          </Text>
        </View>
      </Card>
      
          }
           

      </View>







<Card style= {{marginTop: 30, marginLeft: 30}}>
  <Text>Comments</Text>
</Card>

        
      


    </View>
  )
}

const styles = StyleSheet.create({


  header: {
    paddingLeft: 30,
    paddingTop: 50
  },

  txtSearch: {
    width: 320,
    height: 50,
    borderRadius: 10,
    outline: 'none',
    backgroundColor: 'lightgrey', 
    paddingLeft: 10,
  },

  txtCards: {
    backgroundColor: 'lightgrey', 
    width: 320,
    height: 50, 
    borderRadius: 10,
    marginLeft: 28,
    marginTop: 25
  },

 
  strokeVid: {
    height:180, 
    width: 315,
    borderRadius: 30,
    marginTop: 50,
    marginLeft: 30
  },

  user: {
    height: 25,
    width: 25,
    borderRadius: 30,
    borderWidth: 1, 
    borderColor: 'grey',
    marginLeft: 100,
    marginTop: 90,
  },
  
  home: {
    height: 25,
    width: 25,
    borderRadius: 30,
    borderWidth: 1, 
    borderColor: 'grey',
    marginLeft: 52,
    marginTop: 88,
  },
  
  play: {
    height: 25,
    width: 25,
    borderRadius: 30, 
    borderColor: 'grey',
    marginLeft: 12,
    marginTop: 13,
  }
  
});
