/**
    * @description      : 
    * @author           : MLab
    * @group            : 
    * @created          : 05/10/2021 - 14:22:53
    * 
    * MODIFICATION LOG
    * - Version         : 1.0.0
    * - Date            : 05/10/2021
    * - Author          : MLab
    * - Modification    : 
**/
import React, {useState} from 'react';
import { Text, View, StyleSheet, TextInput, SafeAreaView, Image, TouchableOpacity } from 'react-native';
import { Card } from 'react-native-paper';
import { AntDesign } from '@expo/vector-icons';
import { Entypo } from '@expo/vector-icons';

import strVid from '../images/stroke-vid.jpg';


export default function Strokes({navigation}) {
    
    const {comments, setComments} = useState('')

    const styleTypes = ['default', 'dark-content', 'light-content'];
  const [visibleStatusBar, setVisibleStatusBar] = useState(false);
  const [styleStatusBar, setStyleStatusBar] = useState(styleTypes[0]);

  const changeVisibilityStatusBar = () => {
    setVisibleStatusBar(!visibleStatusBar);
  };

  const changeStyleStatusBar = () => {
    const styleId = styleTypes.indexOf(styleStatusBar) + 1;

    if (styleId === styleTypes.length) {
      return setStyleStatusBar(styleTypes[0]);
    }
    return setStyleStatusBar(styleTypes[styleId]);
  };

  return (
    <View style= {styles.contain}> 


      <Card style={styles.txtCards}>

          <View style={{ flexDirection: 'row'}}> 
        
            <AntDesign name="search1" size={18} color="black"  style= {{marginTop:15, marginLeft: 8}}/>
        
            <TextInput style={styles.txtSearch} 
              name= 'search' placeholder= 'Search' 
            />

          </View>

      </Card>

  
          
        <View>

          <Image style={styles.strokeVid} source= {strVid} />           
          
        </View>



      


      
      <View style={styles.buttonContainer}>

      {!visibleStatusBar ? (
            
            
            <View style= {{flexDirection: 'row', paddingLeft: 50, marginTop: 10}}>

              <Text style={{fontWeight: 'bold'}}>
                Stroke Emergency Video
              </Text>
              <TouchableOpacity title="topNav" onPress={() => changeVisibilityStatusBar()} > 
  
                <AntDesign name="downcircle" size={24} color="black"  style= {styles.dropDown}/>
  
              </TouchableOpacity>



              <View>

                <Entypo name="thumbs-down" size={24} color="black" />

              </View>
  
        </View>
           
          ) 
            
          : //Hidden Step-by-steps
      
          <View>

          <View style= {{paddingLeft: 310, marginTop: 10}}>

            <TouchableOpacity title="topNav" onPress={() => changeVisibilityStatusBar()} > 
  
            <AntDesign name="upcircle" size={24} color="black" />
  
          </TouchableOpacity>

          </View>
  
        

          <Card style={{width: 315, height: 300, marginLeft: 20,
                          borderRadius: 20,
                          backgroundColor: 'pink',
          }}>

        <Text
          style={{
            paddingLeft: 10,
            paddingTop: 10,
            fontWeight: 'bold',
            fontSize: 16,
          }}>
          Description:
        </Text>

        <View
          style={{
            marginTop: 10,
            marginLeft: 12,
            width: 255,
          }}>
          
            
            <Text >
               Stroke Emergency Video
            </Text>

            <Text style= {{fontSize: 10, color: 'gray'}}>
               1 000 000 Views
            </Text>

        </View>

        <View
          style={{
            flexDirection: 'row',
            marginTop: 10,
            marginLeft: 12,
            width: 255,
          }}>
          <Text>
            
            <SafeAreaView style={{ fontWeight: 'bold' }}>
              prince4code:
            </SafeAreaView>
            the app is awesome guys, I give you guys 10/10
          </Text>
        </View>
      </Card>
      </View>
      
      
          }

          
           

      </View>









<Card style= {{marginTop: 30, marginLeft: 30}}>
  <Text>Comments</Text>
</Card>

        
      


    </View>
  )
}

const styles = StyleSheet.create({


  header: {
    paddingLeft: 30,
    paddingTop: 50
  },

  txtSearch: {
    width: 320,
    height: 50,
    borderRadius: 10,
    outline: 'none',
    backgroundColor: 'lightgrey', 
    paddingLeft: 10,
  },

  txtCards: {
    backgroundColor: 'lightgrey', 
    width: 320,
    height: 50, 
    borderRadius: 10,
    marginLeft: 28,
    marginTop: 25
  },

 
  strokeVid: {
    height:180, 
    width: 315,
    borderRadius: 30,
    marginTop: 50,
    marginLeft: 30
  },

  dropDown: {
    marginLeft: 100
  }
  
});
